import React, { useState, useEffect } from "react";

const Issues = () => {
  const [evaluationData, setEvaluationData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvaluationData = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/model-evaluation");
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setEvaluationData(data);
        setError(null);
      } catch (err) {
        console.error("Error fetching model evaluation:", err);
        setError(err);
      }
    };

    fetchEvaluationData();
  }, []);

  if (error) {
    return <div>Error loading evaluation data: {error.message}</div>;
  }

  if (!evaluationData) {
    return <div>Loading model evaluation data...</div>;
  }

  return (
    <div className="card issues-card">
      <h2>Model Evaluation</h2>
      {evaluationData.classification_report && (
        <div>
          <h3>Classification Report</h3>
          <pre>{JSON.stringify(evaluationData.classification_report, null, 2)}</pre>
        </div>
      )}

      {evaluationData.confusion_matrix && evaluationData.classes && (
        <div>
          <h3>Confusion Matrix</h3>
          <div className="table-container">
            <table className="issues-table">
              <thead>
                <tr>
                  <th></th>
                  {evaluationData.classes.map((c) => (
                    <th key={c}>Predicted {c}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {evaluationData.classes.map((actualClass, index) => (
                  <tr key={actualClass}>
                    <th>Actual {actualClass}</th>
                    {evaluationData.confusion_matrix[index].map((count, i) => (
                      <td key={i}>{count}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {evaluationData.confusion_matrix_image && (
        <div>
          <h3>Confusion Matrix Plot</h3>
          <img
            src={`data:image/png;base64,${evaluationData.confusion_matrix_image}`}
            alt="Confusion Matrix"
            style={{ maxWidth: '100%' }}
          />
        </div>
      )}
    </div>
  );
};

export default Issues;
