import React, { useState, useEffect } from "react";

const Issues = () => {
  const [predictedAnomalies, setPredictedAnomalies] = useState([]);
  const [evaluationError, setEvaluationError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvaluationData = async () => {
      setLoading(true);
      setEvaluationError(null);
      try {
        const response = await fetch("http://127.0.0.1:5001/model-evaluation");
        if (!response.ok) {
          const errorText = await response.text();
          console.error("HTTP error fetching evaluation:", response.status, errorText);
          throw new Error(`HTTP error! Status: ${response.status}, Body: ${errorText}`);
        }
        const data = await response.json();
        // Assuming the backend now returns {"anomalies": [{"type": "Anomaly Type 1"}, {"type": "Anomaly Type 2"}, ...]}
        if (data && data.anomalies) {
          setPredictedAnomalies(data.anomalies);
        } else {
          console.warn("Model evaluation data format unexpected:", data);
          setEvaluationError(new Error("Unexpected data format for model evaluation."));
        }
      } catch (error) {
        console.error("Error fetching model evaluation:", error);
        setEvaluationError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchEvaluationData();
  }, []);

  return (
    <div className="card evaluation-card">
      <h2>ðŸ”® Predicted Anomaly Types</h2>
      {loading ? (
        <p>Loading predicted anomaly types...</p>
      ) : evaluationError ? (
        <p className="error">Error loading predictions: {evaluationError.message}</p>
      ) : predictedAnomalies.length === 0 ? (
        <p>No anomalies predicted.</p>
      ) : (
        <div className="table-container">
          <table className="evaluation-table">
            <thead>
              <tr>
                <th>Anomaly Type</th>
              </tr>
            </thead>
            <tbody>
              {predictedAnomalies.map((anomaly, index) => (
                <tr key={index}>
                  <td>{anomaly.type}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Issues;
