import React, { useState, useEffect } from "react";
import './Issues.css';

const Issues = () => {
  const [nodeCpuUtil, setNodeCpuUtil] = useState("");
  const [containerCpuUtil, setContainerCpuUtil] = useState("");
  const [nodeMemoryUtil, setNodeMemoryUtil] = useState("");
  const [containerMemoryUtil, setContainerMemoryUtil] = useState("");
  const [nodeDiskRead, setNodeDiskRead] = useState("");
  const [nodeDiskWrite, setNodeDiskWrite] = useState("");
  const [diskIoTime, setDiskIoTime] = useState("");
  const [networkReceive, setNetworkReceive] = useState("");
  const [networkTransmit, setNetworkTransmit] = useState("");
  const [networkDrops, setNetworkDrops] = useState("");
  const [podCpuLimits, setPodCpuLimits] = useState("");
  const [podCpuRequests, setPodCpuRequests] = useState("");
  const [podMemoryRequests, setPodMemoryRequests] = useState("");
  const [podMemoryLimits, setPodMemoryLimits] = useState("");
  const [activeAlerts, setActiveAlerts] = useState("");
  const [pendingAlerts, setPendingAlerts] = useState("");
  const [podCrashEvents, setPodCrashEvents] = useState("");
  const [nodeFailures, setNodeFailures] = useState("");
  const [cpuManagerErrors, setCpuManagerErrors] = useState("");
  const [memoryManagerErrors, setMemoryManagerErrors] = useState("");
  const [predictedAnomaly, setPredictedAnomaly] = useState(null);
  const [predictionError, setPredictionError] = useState(null);
  const [loadingPrediction, setLoadingPrediction] = useState(false);
  const [anomalySolutions, setAnomalySolutions] = useState({});

  // Placeholder for anomaly solutions - replace with your actual data or API call
  const solutionsData = {
    "CPU Usage Spike": "Check for runaway processes, scale up CPU resources, optimize application code.",
    "Memory Exhaustion": "Identify memory leaks, increase memory limits, restart affected pods.",
    "Network Latency Increase": "Investigate network congestion, check DNS resolution, review firewall rules.",
    "Pod Crash Loop": "Examine pod logs for errors, check resource limits, ensure application health checks are correct.",
    "Disk Full Warning": "Clean up unnecessary files, increase disk space, review log rotation policies.",
    // Add more anomaly types and their solutions here
  };

  useEffect(() => {
    setAnomalySolutions(solutionsData);
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    // ... (rest of your handleInputChange function remains the same)
    switch (name) {
      case "node_cpu_utilization":
        setNodeCpuUtil(value);
        break;
      case "container_cpu_usage":
        setContainerCpuUtil(value);
        break;
      case "node_memory_utilization":
        setNodeMemoryUtil(value);
        break;
      case "container_memory_usage":
        setContainerMemoryUtil(value);
        break;
      case "node_disk_read":
        setNodeDiskRead(value);
        break;
      case "node_disk_write":
        setNodeDiskWrite(value);
        break;
      case "disk_io_time":
        setDiskIoTime(value);
        break;
      case "network_receive":
        setNetworkReceive(value);
        break;
      case "network_transmit":
        setNetworkTransmit(value);
        break;
      case "network_drops":
        setNetworkDrops(value);
        break;
      case "pod_cpu_limits":
        setPodCpuLimits(value);
        break;
      case "pod_cpu_requests":
        setPodCpuRequests(value);
        break;
      case "pod_memory_requests":
        setPodMemoryRequests(value);
        break;
      case "pod_memory_limits":
        setPodMemoryLimits(value);
        break;
      case "active_alerts":
        setActiveAlerts(value);
        break;
      case "pending_alerts":
        setPendingAlerts(value);
        break;
      case "pod_crash_events":
        setPodCrashEvents(value);
        break;
      case "node_failures":
        setNodeFailures(value);
        break;
      case "cpu_manager_errors":
        setCpuManagerErrors(value);
        break;
      case "memory_manager_errors":
        setMemoryManagerErrors(value);
        break;
      default:
        break;
    }
  };

  const predictAnomaly = async () => {
    setLoadingPrediction(true);
    setPredictionError(null);
    setPredictedAnomaly(null);

    try {
      const requestData = {
        node_cpu_utilization: nodeCpuUtil,
        container_cpu_usage: containerCpuUtil,
        node_memory_utilization: nodeMemoryUtil,
        container_memory_usage: containerMemoryUtil,
        node_disk_read: nodeDiskRead,
        node_disk_write: nodeDiskWrite,
        disk_io_time: diskIoTime,
        network_receive: networkReceive,
        network_transmit: networkTransmit,
        network_drops: networkDrops,
        pod_cpu_limits: podCpuLimits,
        pod_cpu_requests: podCpuRequests,
        pod_memory_requests: podMemoryRequests,
        pod_memory_limits: podMemoryLimits,
        active_alerts: activeAlerts,
        pending_alerts: pendingAlerts,
        pod_crash_events: podCrashEvents,
        node_failures: nodeFailures,
        cpu_manager_errors: cpuManagerErrors,
        memory_manager_errors: memoryManagerErrors,
      };

      const response = await fetch("http://127.0.0.1:5001/predict-anomaly", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(requestData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error("Prediction error:", errorData);
        throw new Error(errorData.error || `HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setPredictedAnomaly(data.predicted_anomaly);

    } catch (error) {
      console.error("Error during prediction:", error);
      setPredictionError(error.message);
    } finally {
      setLoadingPrediction(false);
    }
  };

  return (
    <div className="prediction-card">
      <h2>⚙️ Anomaly Prediction</h2>
      <div className="input-fields">
        {/* ... (your input fields remain the same) */}
        <div className="input-row">
          <label>Node CPU Utilization:</label>
          <input type="number" name="node_cpu_utilization" value={nodeCpuUtil} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Container CPU Usage:</label>
          <input type="number" name="container_cpu_usage" value={containerCpuUtil} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Node Memory Utilization:</label>
          <input type="number" name="node_memory_utilization" value={nodeMemoryUtil} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Container Memory Usage:</label>
          <input type="number" name="container_memory_usage" value={containerMemoryUtil} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Node Disk Read:</label>
          <input type="number" name="node_disk_read" value={nodeDiskRead} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Node Disk Write:</label>
          <input type="number" name="node_disk_write" value={nodeDiskWrite} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Disk IO Time:</label>
          <input type="number" name="disk_io_time" value={diskIoTime} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Network Receive:</label>
          <input type="number" name="network_receive" value={networkReceive} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Network Transmit:</label>
          <input type="number" name="network_transmit" value={networkTransmit} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Network Drops:</label>
          <input type="number" name="network_drops" value={networkDrops} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Pod CPU Limits:</label>
          <input type="number" name="pod_cpu_limits" value={podCpuLimits} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Pod CPU Requests:</label>
          <input type="number" name="pod_cpu_requests" value={podCpuRequests} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Pod Memory Requests:</label>
          <input type="number" name="pod_memory_requests" value={podMemoryRequests} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Pod Memory Limits:</label>
          <input type="number" name="pod_memory_limits" value={podMemoryLimits} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Active Alerts:</label>
          <input type="number" name="active_alerts" value={activeAlerts} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Pending Alerts:</label>
          <input type="number" name="pending_alerts" value={pendingAlerts} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Pod Crash Events:</label>
          <input type="number" name="pod_crash_events" value={podCrashEvents} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Node Failures:</label>
          <input type="number" name="node_failures" value={nodeFailures} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>CPU Manager Errors:</label>
          <input type="number" name="cpu_manager_errors" value={cpuManagerErrors} onChange={handleInputChange} />
        </div>
        <div className="input-row">
          <label>Memory Manager Errors:</label>
          <input type="number" name="memory_manager_errors" value={memoryManagerErrors} onChange={handleInputChange} />
        </div>
      </div>

      <button onClick={predictAnomaly} disabled={loadingPrediction} className="predict-button">
        {loadingPrediction ? "Predicting..." : "Predict Anomaly"}
      </button>

      {predictionError && <p className="error">{predictionError.message}</p>}
      {predictedAnomaly && (
        <div className="prediction-result">
          <h3>Predicted Anomaly Type:</h3>
          <p className="predicted-type">{predictedAnomaly}</p>
          {anomalySolutions[predictedAnomaly] && (
            <div className="anomaly-solution">
              <h3>Possible Solutions:</h3>
              <p>{anomalySolutions[predictedAnomaly]}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Issues;
